import React, { useState, useEffect } from 'react';
import { fetchFilteredDataWithNames } from '../services/api';
import LineChart from './Charts/LineChart';
import PieChart from './Charts/PieChart';
import BarChart from './Charts/BarChart';
import Filters from './Filters';
import DataTable from './DataTable';
import { Grid, Paper, Typography } from '@mui/material';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [graphData, setGraphData] = useState([]);
  const [filters, setFilters] = useState({});

  const applyFilters = () => {
    fetchFilteredData(filters)
      .then((response) => {
        console.log('API Response:', response.data);

        // Ensure data is in the correct format
        const mappedData = Array.isArray(response.data)
          ? response.data
          : response.data.values || []; // Adjust based on the API response structure

        setData(mappedData);

        // Prepare graph data
        const graphData = {
          pie: Object.values(
            mappedData.reduce((acc, item) => {
              acc[item.paytype_name] = acc[item.paytype_name] || { name: item.paytype_name, value: 0 };
              acc[item.paytype_name].value += item.amount;
              return acc;
            }, {})
          ),
          bar: Object.values(
            mappedData.reduce((acc, item) => {
              acc[item.provider_name] = acc[item.provider_name] || { name: item.provider_name, value: 0 };
              acc[item.provider_name].value += item.amount;
              return acc;
            }, {})
          ),
          line: mappedData.map((item) => ({
            date: item.date,
            amount: item.amount,
          })),
        };

        setGraphData(graphData);
      })
      .catch((error) => {
        console.error('Error Fetching Data:', error);
        setData([]); // Reset data on error
      });
  };


  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: 'auto' }}>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>

      {/* Filters */}
      <Filters filters={filters} setFilters={setFilters} onApplyFilters={applyFilters} />

      {/* Table */}
      <DataTable data={data} />

      {/* Charts */}
      <Grid container spacing={2} style={{ marginTop: '20px' }}>
        <Grid item xs={12} sm={6}>
          <Paper style={{ padding: '10px', height: '300px' }}>
            <Typography variant="h6" gutterBottom>
              Pay Type Distribution
            </Typography>
            <PieChart data={graphData.pie} />
          </Paper>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Paper style={{ padding: '10px', height: '300px' }}>
            <Typography variant="h6" gutterBottom>
              Provider Contributions
            </Typography>
            <BarChart data={graphData.bar} />
          </Paper>
        </Grid>
        <Grid item xs={12}>
          <Paper style={{ padding: '10px', height: '400px' }}>
            <Typography variant="h6" gutterBottom>
              Trends Over Time
            </Typography>
            <LineChart data={graphData.line} />
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

export default Dashboard;
