import React, { useState, useEffect } from 'react';
import { fetchAggregates, fetchFilteredData } from '../services/api';
import LineChart from './Charts/LineChart';
import PieChart from './Charts/PieChart';
import BarChart from './Charts/BarChart';
import Filters from './Filters';
import DataTable from './DataTable';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [aggregates, setAggregates] = useState({});
  const [filters, setFilters] = useState({});

  const applyFilters = () => {
    console.log('Applying Filters:', filters);
    fetchFilteredData(filters)
      .then((response) => {
        console.log('Filtered Data Response:', response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error Fetching Data:', error);
      });
  };

  useEffect(() => {
    fetchAggregates('paytype_id').then((response) => setAggregates(response.data));
  }, [filters]);

  return (
    <div style={{ padding: '20px' }}>
      <h1 style={{ textAlign: 'center' }}>Dashboard</h1>

      {/* Filters */}
      <Filters filters={filters} setFilters={setFilters} onApplyFilters={applyFilters} />

      {/* Table */}
      <DataTable data={data} />

      {/* Charts */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-evenly',
          flexWrap: 'wrap',
          marginTop: '20px',
        }}
      >
        {/* Responsive Chart Containers */}
        <div
          style={{
            width: '45%',
            minWidth: '300px',
            height: '300px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <PieChart data={aggregates} />
        </div>
        <div
          style={{
            width: '45%',
            minWidth: '300px',
            height: '300px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <LineChart data={aggregates} />
        </div>
        <div
          style={{
            width: '90%',
            minWidth: '300px',
            height: '300px',
            marginTop: '20px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <BarChart data={aggregates} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
