import axios from 'axios';

const BASE_URL = 'http://127.0.0.1:8000/api';

// Fetch raw filtered data from the API
export const fetchFilteredData = (params) => {
  return axios.get(`${BASE_URL}/filtered-data/`, { params });
};

// Fetch filtered data with mapped names
export const fetchFilteredDataWithNames = (params) => {
  return axios
    .get(`${BASE_URL}/filtered-data/`, { params })
    .then((response) => {
      const data = response.data.values || []; // Assuming `values` contains the filtered data
      const categories = response.data.categories || {}; // Assuming `categories` contains mappings

      // Map names for IDs
      const mappedData = data.map((item) => ({
        ...item,
        paytype_name: categories.paytype_id[item.paytype_id],
        provider_name: categories.provider_id[item.provider_id],
        employee_type_name: categories.employee_type_id[item.employee_type_id],
      }));

      return mappedData;
    });
};

// Fetch aggregate data for grouping
export const fetchAggregates = (groupBy) => {
  return axios.get(`${BASE_URL}/filtered-data/`, { params: { type: 'aggregates', group_by: groupBy } });
};

// Fetch categories (mapping of IDs to names)
export const fetchCategories = () => {
  return axios.get(`${BASE_URL}/filtered-data/`, { params: { type: 'categories' } });
};
