import React, { useEffect, useState } from 'react';
import { TextField, MenuItem, Button, Checkbox, FormControlLabel } from '@mui/material';
import { fetchCategories } from '../services/api';

const Filters = ({ filters, setFilters, onApplyFilters }) => {
  const [categories, setCategories] = useState({ paytypes: [], providers: [], employeeTypes: [] });

  useEffect(() => {
    fetchCategories()
      .then((response) => {
        setCategories({
          paytypes: Object.entries(response.data.paytype_id).map(([id, name]) => ({ id, name })),
          providers: Object.entries(response.data.provider_id).map(([id, name]) => ({ id, name })),
          employeeTypes: Object.entries(response.data.employee_type_id).map(([id, name]) => ({ id, name })),
        });
      })
      .catch((error) => console.error('Error Fetching Categories:', error));
  }, []);

  const handleFilterChange = (name, value) => {
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
      <TextField
        label="Start Date"
        type="date"
        value={filters.start_date || ''}
        onChange={(e) => handleFilterChange('start_date', e.target.value)}
        InputLabelProps={{ shrink: true }}
      />
      <TextField
        label="End Date"
        type="date"
        value={filters.end_date || ''}
        onChange={(e) => handleFilterChange('end_date', e.target.value)}
        InputLabelProps={{ shrink: true }}
      />
      <TextField
        label="Min Amount"
        type="number"
        value={filters.min_amount || ''}
        onChange={(e) => handleFilterChange('min_amount', e.target.value)}
      />
      <TextField
        label="Max Amount"
        type="number"
        value={filters.max_amount || ''}
        onChange={(e) => handleFilterChange('max_amount', e.target.value)}
      />
      <TextField
        select
        label="Pay Type"
        value={filters.paytype_id || ''}
        onChange={(e) => handleFilterChange('paytype_id', e.target.value)}
      >
        {categories.paytypes.map((paytype) => (
          <MenuItem key={paytype.id} value={paytype.id}>
            {paytype.name}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Provider"
        value={filters.provider_id || ''}
        onChange={(e) => handleFilterChange('provider_id', e.target.value)}
      >
        {categories.providers.map((provider) => (
          <MenuItem key={provider.id} value={provider.id}>
            {provider.name}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Employee Type"
        value={filters.employee_type_id || ''}
        onChange={(e) => handleFilterChange('employee_type_id', e.target.value)}
      >
        {categories.employeeTypes.map((type) => (
          <MenuItem key={type.id} value={type.id}>
            {type.name}
          </MenuItem>
        ))}
      </TextField>
      <FormControlLabel
        control={
          <Checkbox
            checked={filters.exclude_non_positive || false}
            onChange={(e) => handleFilterChange('exclude_non_positive', e.target.checked)}
          />
        }
        label="Exclude Non-Positive Amounts"
      />
      <Button variant="contained" onClick={onApplyFilters}>
        Apply Filters
      </Button>
    </div>
  );
};

export default Filters;
