const chartData = {
  labels: data.map((item) => item.name),
  datasets: [
    {
      label: 'Total Amount',
      data: data.map((item) => item.value),
      backgroundColor: data.map(() => `hsl(${Math.random() * 360}, 50%, 70%)`),
    },
  ],
};
