import React, { useState, useEffect } from 'react';
import { fetchAggregates, fetchFilteredData } from '../services/api';
import LineChart from './Charts/LineChart';
import PieChart from './Charts/PieChart';
import BarChart from './Charts/BarChart';
import Filters from './Filters';
import DataTable from './DataTable';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [aggregates, setAggregates] = useState({});
  const [filters, setFilters] = useState({});
  const [groupBy, setGroupBy] = useState('paytype_id'); // Default group by field

  const applyFilters = () => {
    console.log('Applying Filters:', filters);
    fetchFilteredData(filters)
      .then((response) => {
        console.log('Filtered Data Response:', response.data);
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error Fetching Data:', error);
      });

    // Fetch aggregated data based on filters and grouping
    fetchAggregates(groupBy, filters)
      .then((response) => {
        console.log('Aggregates Response:', response.data);
        setAggregates(response.data);
      })
      .catch((error) => {
        console.error('Error Fetching Aggregates:', error);
      });
  };

  useEffect(() => {
    // Initial fetch for aggregates
    fetchAggregates(groupBy, filters).then((response) => setAggregates(response.data));
  }, [groupBy, filters]);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Dashboard</h1>

      {/* Filters */}
      <Filters filters={filters} setFilters={setFilters} onApplyFilters={applyFilters} />

      {/* Group By Dropdown */}
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="groupBy">Group By:</label>
        <select
          id="groupBy"
          value={groupBy}
          onChange={(e) => setGroupBy(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px' }}
        >
          <option value="paytype_id">Pay Type</option>
          <option value="provider_id">Provider</option>
          <option value="employee_type_id">Employee Type</option>
        </select>
      </div>

      {/* Table */}
      <DataTable data={data} />

      {/* Charts */}
      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
        <PieChart data={aggregates} />
        <LineChart data={aggregates} />
        <BarChart data={aggregates} />
      </div>
    </div>
  );
};

export default Dashboard;
