import React from 'react';
import { Bar } from 'react-chartjs-2';

const BarChart = ({ data }) => {
  const chartData = {
    labels: Object.keys(data),
    datasets: [
      {
        label: 'Total Amount',
        data: Object.values(data).map((group) => group.total_amount),
        backgroundColor: 'orange',
      },
    ],
  };

  return <Bar data={chartData} />;
};

export default BarChart;
