import React, { useState, useEffect } from 'react';
import { fetchFilteredData, fetchCategories } from '../services/api';
import LineChart from './Charts/LineChart';
import PieChart from './Charts/PieChart';
import Filters from './Filters';
import DataTable from './DataTable';
import { Grid, Paper, Typography } from '@mui/material';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [graphData, setGraphData] = useState({ pie: [], bar: [], line: [] });
  const [filters, setFilters] = useState({});
  const [categories, setCategories] = useState(null);

  // Fetch categories once on mount
  useEffect(() => {
    fetchCategories()
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => console.error('Error fetching categories:', error));
  }, []);

  const applyFilters = () => {
    if (!categories) {
      console.error('Categories not loaded yet');
      return;
    }

    fetchFilteredData(filters)
      .then((response) => {
        const mappedData = response.data.map((item) => ({
          ...item,
          paytype_name: categories.paytype_id[item.paytype_id],
          provider_name: categories.provider_id[item.provider_id],
          employee_type_name: categories.employee_type_id[item.employee_type_id],
        }));

        setData(mappedData);

        // Generate graph data
        const pieData = Object.values(
          mappedData.reduce((acc, item) => {
            acc[item.paytype_name] = acc[item.paytype_name] || { name: item.paytype_name, value: 0 };
            acc[item.paytype_name].value += item.amount;
            return acc;
          }, {})
        );

        const lineData = mappedData.reduce((acc, item) => {
          const existing = acc.find((entry) => entry.date === item.date);
          if (existing) {
            existing.amount += item.amount;
          } else {
            acc.push({ date: item.date, amount: item.amount });
          }
          return acc;
        }, []);

        setGraphData({ pie: pieData, line: lineData });
      })
      .catch((error) => console.error('Error fetching filtered data:', error));
  };

  const resetFilters = () => {
    setFilters({});
    setData([]);
    setGraphData({ pie: [], line: [] });
  };

  return (
    <div className="dashboard-container">
      <Typography variant="h4" className="dashboard-title">
        Professional Dashboard
      </Typography>

      <Filters filters={filters} setFilters={setFilters} onApplyFilters={applyFilters} onResetFilters={resetFilters} />

      <DataTable data={data} />

      <Grid container spacing={3} className="charts-container">
        <Grid item xs={12} sm={6}>
          <Paper className="chart-paper" style={{ maxHeight: '400px', overflow: 'hidden' }}>
            <Typography variant="h6" className="chart-title">
              Pay Type Distribution
            </Typography>
            <PieChart data={graphData.pie} />
          </Paper>
        </Grid>
        <Grid item xs={12}>
          <Paper className="chart-paper" style={{ maxHeight: '400px', overflow: 'hidden' }}>
            <Typography variant="h6" className="chart-title">
              Trends Over Time
            </Typography>
            <LineChart data={graphData.line} />
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
};

export default Dashboard;
