import React, { useEffect, useState } from 'react';
import { TextField, MenuItem, Button, Checkbox, FormControlLabel } from '@mui/material';
import { fetchCategories } from '../services/api';
import '../styles/Filters.css';

const Filters = ({ filters, setFilters, onApplyFilters, onResetFilters }) => {
  const [paytypes, setPaytypes] = useState([]);
  const [providers, setProviders] = useState([]);
  const [employeeTypes, setEmployeeTypes] = useState([]);

  useEffect(() => {
    fetchCategories()
      .then((response) => {
        const data = response.data;
        setPaytypes(Object.entries(data.paytype_id).map(([id, name]) => ({ id, name })));
        setProviders(Object.entries(data.provider_id).map(([id, name]) => ({ id, name })));
        setEmployeeTypes(Object.entries(data.employee_type_id).map(([id, name]) => ({ id, name })));
      })
      .catch((error) => {
        console.error('Error Fetching Categories:', error);
      });
  }, []);

  const handleFilterChange = (name, value) => {
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  return (
    <div className="filters-container">
      <TextField
        label="Start Date"
        type="date"
        value={filters.start_date || ''}
        onChange={(e) => handleFilterChange('start_date', e.target.value)}
        InputLabelProps={{ shrink: true }}
      />
      <TextField
        label="End Date"
        type="date"
        value={filters.end_date || ''}
        onChange={(e) => handleFilterChange('end_date', e.target.value)}
        InputLabelProps={{ shrink: true }}
      />
      <TextField
        select
        label="Pay Type"
        value={filters.paytype_id || ''}
        onChange={(e) => handleFilterChange('paytype_id', e.target.value)}
      >
        <MenuItem value="">Select Pay Type</MenuItem>
        {paytypes.map((paytype) => (
          <MenuItem key={paytype.id} value={paytype.id}>
            {paytype.name}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Provider"
        value={filters.provider_id || ''}
        onChange={(e) => handleFilterChange('provider_id', e.target.value)}
      >
        <MenuItem value="">Select Provider</MenuItem>
        {providers.map((provider) => (
          <MenuItem key={provider.id} value={provider.id}>
            {provider.name}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        select
        label="Employee Type"
        value={filters.employee_type_id || ''}
        onChange={(e) => handleFilterChange('employee_type_id', e.target.value)}
      >
        <MenuItem value="">Select Employee Type</MenuItem>
        {employeeTypes.map((employeeType) => (
          <MenuItem key={employeeType.id} value={employeeType.id}>
            {employeeType.name}
          </MenuItem>
        ))}
      </TextField>
      <FormControlLabel
        control={
          <Checkbox
            checked={filters.exclude_non_positive || false}
            onChange={(e) => handleFilterChange('exclude_non_positive', e.target.checked)}
          />
        }
        label="Exclude Non-Positive Amounts"
      />
      <div className="filter-actions">
        <Button variant="contained" onClick={onApplyFilters}>
          Apply Filters
        </Button>
        <Button variant="outlined" onClick={onResetFilters} style={{ marginLeft: '10px' }}>
          Reset Filters
        </Button>
      </div>
    </div>
  );
};

export default Filters;
